, 
[8151] = {Title = "이국의 방랑자", 
Description = {"내가 모스코비아를 떠나 이 곳으로 여행을 올 때 같이 온 사람이로군. 이 곳까지 같이 왔었는데 얼마 전에 헤어졌소. 사랑하는 여인을 잊지 못 하고 이곳 저곳 그냥 방황하는 모양이더군. 음... 떠나기 전에 어디더라...? 여하튼 사막을 여행하고 싶다고 얘길 했던 듯 허이."}
, Summary = "이고르를 찾는다"}
, 
[8152] = {Title = "모로크 마을주민", 
Description = {"혹시 좀 음울한 분위기에 이상한 옷을 입은 그 친구를 말하는겁니까? 억양이 이상한 게 룬 미드가츠 왕국 내의 사람은 아니었던 것 같던데... 얼마 전까지 여기 있었는데, 피라미드를 보러 간다고 떠나버렸습니다. 그 이후로는 보지 못했죠. 거긴 위험하다고 가지 말라고 했는데 결국 가버리더군요."}
, Summary = "이고르를 찾는다"}
, 
[8153] = {Title = "이고르의 전언", 
Description = {"먼저 가셔서 스베뜰라나에게 이 말을 좀 전해주시겠습니까? 달이 가득차는 날까진 모스코비아에 돌아갈테니, 한번만이라도 만나달라고요. 그녀를 괴롭게 하지 않을 정도로 강해지고 행복해져서, 그녀를 찾아가겠다고요."}
, Summary = ""}
, 
[8155] = {Title = "겨울의 주인 마로즈까", 
