[8156] = {Title = "겨울의 주인 마로즈까", 
Description = {"들었는지 모르겠지만 황금에서 가늘고 길게 실을 뽑아내는 건 매우 어려운 일이야. 시간도 엄청 오래 걸리고 말이지. 내 지금부터 제작을 시작할테니, 어디보자... 한시간쯤 뒤에 와 주겠나?"}
, Summary = ""}
, 
[8157] = {Title = "마로즈까의 조건", 
Description = {"자네가 마리아 모레브나를 도우려고 했기 때문에 이미 코쉐이는 자네를 적대시 할 거야. 그래도 금실을 원하나? 그래, 금실을 주지! 하지만 내가 내는 수수께끼를 모두 맞추지 않으면 줄 수 없네. 우리 마을을 위해 자네를 희생하게 만들 순 없네. 그를 위한 최소한의 시험이라고 생각하게나."}
, Summary = ""}
, 
[8158] = {Title = "낡은 오두막의 바바야가", 
Description = {"깊고 깊은 숲의 가장자리에 낡은 오두막이 있다고 하더군. 그 오두막 근처에 \'황금색의 열쇠\'를 만들 수 있는 자가 있다고 했어. ^ff0000냇물아, 냇물아, 내 사마귀를 마셔라^000000 바바야가로부터 몸을 지킬 수 있는 주문이야. 이 말을 꼭 기억해 두라고."}
, Summary = ""}
, 
[8159] = {Title = "바바야가의 심부름", 
Description = {"내가 \'황금색의 열쇠\'를 만들 동안, 네게 심부름을 하나 맡기겠다! 내가 키우는 소가 집을 나갔어. 그애를 좀 찾아오도록 해. 찾으면 소에게 귀환 주문을 걸어. \'^ff0000좋은 사료는 오렌지 향^000000\'이야. 자! 어서 움직여! 느릿느릿하면 혼을 내줄테다! 이히히히히히히."}
