, 
[8171] = {Title = "바바야가의 심부름", 
Description = {"오두막 옆에 저기 저 단지 보이나? 그 안에 집귀신이 살고 있지. 그녀석을 좀 쫓아내고 와. 하도 들러붙어서 귀찮아 죽겠어. 자! 어서 움직여! 느릿느릿하면 혼을 내줄테다! 이히히히히히히히."}
, Summary = "집귀신을 쫓아낸다"}
, 
[8172] = {Title = "바바야가의 심부름", 
Description = {"앞으로는 사람들에게 해꼬지 같은 거 안하고, 착하게 지낼게요! 이 집을 지켜주는 수호령이 되겠어요! 일개 집 귀신과 어울려 준 당신의 상냥한 마음, 앞으로도 잊지 않을게요. 다시 한 번 정말 고맙습니다."}
, Summary = "바바야가에게 돌아간다"}
, 
[8173] = {Title = "바바야가의 심부름", 
Description = {"페이욘에 있는 빗자루 할머니에게 빗자루좀 사와. 청소는 페이욘제 빗자루가 제대론데 시간이 없어서 가질 못했어. 자! 어서 움직여! 느릿느릿하면 혼을 내줄테다! 이히히히히히히히."}
, Summary = "페이욘에서 빗자루를 사온다"}
, 
[8174] = {Title = "바바야가의 심부름", 
Description = {"얼마 전 도깨비에게 갓 만든 빗자루를 몽땅 뺏기고 말았다우. 도깨비도 맘에 들어할만큼 빗자루가 잘 만들어져 기쁘긴 한데, 그걸 빼앗겨서야... 몇 번이나 빗자루를 되찾으러 우리 아들놈이 갔었지만, 도깨비가 나무 속에 숨어서 장난질만 쳐대는 바람에 헛수고만 하고 왔다지 뭐유."}
, Summary = ""}
, 
[8175] = {Title = "바바야가의 심부름", 
Description = {"아아. 져버렸네. 그래도 재미있었어. 그럼 재미있게 놀았으니, 약속대로 빗자루를 내어주도록 하지. 너 재미있는 인간이야. 다음에도 기회가 닿으면 또 놀아줘. 그럼 자 이걸 받아."}
, Summary = "바바야가에게 돌아간다"}
, 
[8176] = {Title = "황금색의 열쇠", 
Description = {"마침 잘 왔어. 나도 \'황금색의 열쇠\'를 만들어 냈지! 자, 이걸로 마리아 모레브나를 도와주도록 해. 코쉐이는 조심하고 말이지. 키히히히히히히."}
, Summary = "마리아 모레브나를 풀어준다"}
, 
[8177] = {Title = "불사신 코쉐이", 
Description = {"크으윽... 지금 손을 쓰면 마리아 모레브나를 살릴 수 있다. 난 마리아 모레브나를 데리고 피할테니, 코쉐이를 부탁하마!!"}
, Summary = "코쉐이를 퇴치한다"}
, 
[8178] = {Title = "살아있는 물과 죽은 물", 
Description = {"마리아는 아직 죽지 않았다. 하지만 살아 있는 것도 아니지. 그녀를 구하려면 \'살아있는 물\'과 \'죽은 물\'이 필요하지. 바바야가라면 그녀를 구할 수 있을 것이다. 내가 그녀를 죽음으로부터 지키고 있을테니, 바바야가에게서 \'살아있는 물\'과 \'죽은 물\'을 받아오길 바란다. 부탁한다."}
, Summary = "살아있는 물과 죽은 물을 가져온다"}
, 
[8179] = {Title = "살아있는 물과 죽은 물", 
Description = {"\'살아있는 물\'... 그건 언젠가 들어본 기억이 있지. ^0000ff성스러운 물 1 개^000000와 ^0000ff이 세상을 구성하고 있는 나무의 잎 2장^000000으로 만들 수 있다고 했지. 그리고 \'죽은 물\'... 이건 ^0000ff저주받은 물 1개^000000와 ^0000ff히나레라는 꽃의 잎사귀 10장^000000으로 만들 수 있을거야."}
, Summary = "바바야가에게 재료를 가져다준다"}
, 
[8180] = {Title = "살아있는 물과 죽은 물", 
Description = {"어서 이것들을 가져가서 마리아 모레브나를 도와주도록 해라, 인간 꼬마야. 키히히힛. 우선 \'죽은 물\'을 마리아 모레브나에게 끼얹어, 상처와 저주를 없애거라. 그리고 \'살아있는 물\'을 끼얹으면 마리아가 살아날게다. 알아들었으면 냉큼 가 보라고! 키히히히히"}
